import React, { useState } from "react";
// import "../styles/appointment_styles.css"; // Add your styles here if needed

const Appointment = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    time: "",
    doctorEmail: "",
    message: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Retrieve JWT token from storage (e.g., localStorage)
    let token = localStorage.getItem("token"); // Replace with your storage method if different
    if (token && token.startsWith("Bearer ")) {
      token = token.slice(7);
    }

    try {
      const response = await fetch("http://localhost:8080/Appointments/createAppointment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: token, // Include JWT token in the Authorization header
        },
        body: JSON.stringify({
          doctorEmail: formData.email, // Map `doctor` field to `doctorEmail`
          date: formData.time, // Map `meetingTime` field to `date`
          reason_for_appointment: formData.message, // Map `message` field to `reason_for_appointment`
        }),
      });
  
      if (!response.ok) {
        // Handle non-200 responses
        const errorText = await response.text(); // Get error response as plain text
        console.error("Server error:", errorText);
        alert(`Error: ${response.status} - ${errorText}`);
        return;
      }
  
      const result = await response.json();
      alert(result[0].message); // Show success message
    } catch (error) {
      console.error("Error creating appointment:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
      <form 
    onSubmit={handleSubmit} 
    className="form-card" 
    style={{
      width: '400px', /* Card width, can adjust as needed */
      margin: '20px auto', /* Centers the card horizontally */
      padding: '20px',
      backgroundColor: '#fcf7fb', /* Background color of the card */
      borderRadius: '10px', /* Rounded corners */
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', /* Soft shadow effect */
      border: '1px solid #ddd' /* Light border for the card */
    }}
  >
    <div 
      className="form-group" 
      style={{ marginBottom: '20px' }} /* Space between input groups */
    >
      <label htmlFor="email">Doctor Email:</label>
      <input
        type="email"
        id="email"
        name="email"
        value={formData.email}
        onChange={handleChange}
        style={{
          width: '100%', /* Full width of the container */
          padding: '10px', /* Padding inside the input */
          marginTop: '5px', /* Space between label and input */
          border: '1px solid #ccc', /* Light border for input fields */
          borderRadius: '5px', /* Rounded corners */
          fontSize: '14px' /* Slightly smaller font for input */
        }}
      />
    </div>
    
    <div 
      className="form-group" 
      style={{ marginBottom: '20px' }} /* Space between input groups */
    >
      <label htmlFor="time">Date:</label>
      <input
        type="datetime-local"
        id="time"
        name="time"
        min="2024-25-07T00:00"
        max="2030-12-14T00:00"
        value={formData.time}
        onChange={handleChange}
        style={{
          width: '100%', /* Full width of the container */
          padding: '10px', /* Padding inside the input */
          marginTop: '5px', /* Space between label and input */
          border: '1px solid #ccc', /* Light border for input fields */
          borderRadius: '5px', /* Rounded corners */
          fontSize: '14px' /* Slightly smaller font for input */
        }}
      />
    </div>

    <div 
      className="form-group" 
      style={{ marginBottom: '20px' }} /* Space between input groups */
    >
      <label htmlFor="message">Reason For Appointment:</label>
      <textarea
        id="message"
        name="message"
        value={formData.message}
        onChange={handleChange}
        style={{
          width: '100%', /* Full width of the container */
          padding: '10px', /* Padding inside the textarea */
          marginTop: '5px', /* Space between label and textarea */
          border: '1px solid #ccc', /* Light border for textarea */
          borderRadius: '5px', /* Rounded corners */
          fontSize: '14px' /* Slightly smaller font for textarea */
        }}
      />
    </div>

    <button 
      type="submit"
      style={{
        padding: '10px 20px', /* Space inside the button */
        backgroundColor: '#6a0dad', /* Purple background */
        color: 'white', /* White text */
        fontSize: '16px', /* Font size for button text */
        border: 'none', /* Remove border */
        borderRadius: '5px', /* Rounded corners for button */
        cursor: 'pointer', /* Changes cursor to pointer on hover */
        transition: 'background-color 0.3s ease' /* Smooth transition for background color */
      }}
      onMouseOver={(e) => e.target.style.backgroundColor = '#9b30b8'} /* Lighter purple when hovered */
      onMouseOut={(e) => e.target.style.backgroundColor = '#6a0dad'} /* Resets to purple when mouse leaves */
    >
      Schedule Appointment
    </button>
  </form>
  );
};

export default Appointment;
