"use client";

import React, { useState } from "react";
import { ColumnDef } from "@tanstack/react-table";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "../../components/ui/table";
import { Button } from "../../components/ui/button";
import { Input } from "../../components/ui/input";
import { useReactTable, getCoreRowModel, getPaginationRowModel } from "@tanstack/react-table";

export type AppointmentEntry = {
  id: string;
  doctor: string;
  reason_for_appointment: string;
  treatment: string;
  date: string;
  allergies: string;
  conditions: string;
  medications: string;
  immunizations: string;
  procedures: string;
};

const AppointmentsTable: React.FC<{ data: AppointmentEntry[] }> = ({ data }) => {
  const [tableData, setTableData] = useState<AppointmentEntry[]>(data);

  const columns: ColumnDef<AppointmentEntry>[] = [
    { accessorKey: "id", header: "Appointment ID" },
    { accessorKey: "doctor", header: "Doctor" },
    { accessorKey: "reason_for_appointment", header: "Reason for Appointment" },
    { accessorKey: "treatment", header: "Treatment" },
    { accessorKey: "date", header: "Date" },
    { accessorKey: "allergies", header: "Allergies" },
    { accessorKey: "conditions", header: "Conditions" },
    { accessorKey: "medications", header: "Medications" },
    { accessorKey: "immunizations", header: "Immunizations" },
    { accessorKey: "procedures", header: "Procedures" },
  ];

  const table = useReactTable({
    data: tableData,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    initialState: { pagination: { pageSize: 10 } }, // Set default page size to 10
  });

  const handleInputChange = (rowIndex: number, columnId: string, value: string) => {
    const updatedData = [...tableData];
    updatedData[rowIndex][columnId as keyof AppointmentEntry] = value;
    setTableData(updatedData);
  };

  const handleSubmit = () => {
    console.log("Submitted Data: ", tableData);
    // Perform API call to submit the updated data
  };

  return (
    <div className="container mx-auto py-10">
      <div className="rounded-md border overflow-auto">
        <Table>
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id}>
                {headerGroup.headers.map((header) => (
                  <TableHead key={header.id}>
                    {header.isPlaceholder
                      ? null
                      : header.column.columnDef.header as string}
                  </TableHead>
                ))}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows.map((row) => (
              <TableRow key={row.id}>
                {row.getVisibleCells().map((cell) => (
                  <TableCell key={cell.id}>
                    {cell.column.id === "id" ? (
                      row.original[cell.column.id]
                    ) : (
                      <Input
                        value={row.original[cell.column.id as keyof AppointmentEntry] as string}
                        onChange={(e) =>
                          handleInputChange(row.index, cell.column.id, e.target.value)
                        }
                      />
                    )}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>

      {/* Pagination Controls */}
      <div className="flex items-center justify-between py-4">
        <Button
          variant="outline"
          onClick={() => table.previousPage()}
          disabled={!table.getCanPreviousPage()}
        >
          Previous
        </Button>
        <span>
          Page {table.getState().pagination.pageIndex + 1} of{" "}
          {table.getPageCount()}
        </span>
        <Button
          variant="outline"
          onClick={() => table.nextPage()}
          disabled={!table.getCanNextPage()}
        >
          Next
        </Button>
      </div>

      {/* Submit Button */}
      <div className="py-4">
        <Button onClick={handleSubmit} className="bg-blue-500 text-white">
          Submit All Changes
        </Button>
      </div>
    </div>
  );
};

export default AppointmentsTable;
