import React, { useState } from "react";
import "../styles/styles.css"; // Add your styles here if needed

const Appointment = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    meetingTime: "",
    department: "",
    doctor: "",
    message: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Retrieve JWT token from storage (e.g., localStorage)
    const token = localStorage.getItem("token"); // Replace with your storage method if different
  
    try {
      const response = await fetch("http://localhost:8080/Appointments/createAppointment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer `+token, // Include JWT token in the Authorization header
        },
        body: JSON.stringify({
          doctorEmail: formData.doctor, // Map `doctor` field to `doctorEmail`
          date: formData.meetingTime, // Map `meetingTime` field to `date`
          reason_for_appointment: formData.message, // Map `message` field to `reason_for_appointment`
        }),
      });
  
      if (!response.ok) {
        // Handle non-200 responses
        const errorText = await response.text(); // Get error response as plain text
        console.error("Server error:", errorText);
        alert(`Error: ${response.status} - ${errorText}`);
        return;
      }
  
      const result = await response.json();
      alert(result[0].message); // Show success message
    } catch (error) {
      console.error("Error creating appointment:", error);
      alert("An error occurred. Please try again.");
    }
  };
  

  return (
    <section id="appointment" className="appointment section-bg">
      <div className="container">
        <div className="section-title">
          <h2>Make an Appointment</h2>
          <p>Please fill in your details to book an appointment</p>
        </div>

        <form onSubmit={handleSubmit} className="php-email-form">
          <div className="row">
            <div className="col-md-4 form-group">
              <input
                type="text"
                name="name"
                className="form-control"
                id="name"
                placeholder="Your Name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-md-4 form-group mt-3 mt-md-0">
              <input
                type="email"
                name="email"
                className="form-control"
                id="email"
                placeholder="Your Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-md-4 form-group mt-3 mt-md-0">
              <input
                type="tel"
                name="phone"
                className="form-control"
                id="phone"
                placeholder="Your Phone"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="row">
            <div className="col-lg-4 form-group mt-6">
              <input
                type="datetime-local"
                name="meetingTime"
                className="form-control"
                id="meeting-time"
                value={formData.meetingTime}
                onChange={handleChange}
                min="2024-11-07T00:00"
                max="2030-12-14T00:00"
                required
              />
            </div>
            <div className="col-md-4 form-group mt-3">
              <select
                name="department"
                id="department"
                className="form-select"
                value={formData.department}
                onChange={handleChange}
                required
              >
                <option value="">Select Department</option>
                <option value="Emergency Department">Emergency Department</option>
                <option value="Cardiology">Cardiology</option>
                <option value="Neurology">Neurology</option>
                <option value="Physical Therapy">Physical Therapy</option>
                <option value="Gynacology">Gynacology</option>
                <option value="Pediatrics">Pediatrics</option>
                <option value="Orthopedics Unit">Orthopedics Unit</option>
                <option value="Gastroenterology">Gastroenterology</option>
                <option value="Dentistry">Dentistry</option>
              </select>
            </div>
            <div className="col-md-4 form-group mt-3">
              <select
                name="doctor"
                id="doctor"
                className="form-select"
                value={formData.doctor}
                onChange={handleChange}
                required
              >
                <option value="">Select Doctor</option>
                <option value="sarah.jhonson@example.com">Dr Sarah Jhonson - Anesthesiologist</option>
                <option value="walter.white@example.com">Dr Walter White - Chief Medical Officer</option>
                <option value="william.anderson@example.com">Dr William Anderson - Cardiology</option>
                <option value="amanda.jepson@example.com">Dr Amanda Jepson - Neurosurgeon</option>
                <option value="kevin.umba@example.com">Dr Kevin Umba - Gynacology</option>
              </select>
            </div>
          </div>

          <div className="form-group mt-3">
            <textarea
              className="form-control"
              name="message"
              rows="5"
              placeholder="Reason for Appointment (Optional)"
              value={formData.message}
              onChange={handleChange}
            ></textarea>
          </div>
          <div className="text-center">
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    </section>
  );
};

export default Appointment;
