import React, { useState } from "react";
import "../styles/styles.css"; // Add your styles here if needed

const Appointment = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    meetingTime: "",
    department: "",
    doctor: "",
    message: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Retrieve JWT token from storage (e.g., localStorage)
    const token = localStorage.getItem("token"); // Replace with your storage method if different
  
    try {
      const response = await fetch("http://localhost:8080/Appointments/createAppointment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer `+token, // Include JWT token in the Authorization header
        },
        body: JSON.stringify({
          doctorEmail: formData.doctor, // Map `doctor` field to `doctorEmail`
          date: formData.meetingTime, // Map `meetingTime` field to `date`
          reason_for_appointment: formData.message, // Map `message` field to `reason_for_appointment`
        }),
      });
  
      if (!response.ok) {
        // Handle non-200 responses
        const errorText = await response.text(); // Get error response as plain text
        console.error("Server error:", errorText);
        alert(`Error: ${response.status} - ${errorText}`);
        return;
      }
  
      const result = await response.json();
      alert(result[0].message); // Show success message
    } catch (error) {
      console.error("Error creating appointment:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
      <form onSubmit={handleSubmit} style={{ width: '300px', margin: '20px', padding: '20px', border: '1px solid #ccc' }}>
      <div style={{ marginBottom: '10px' }}>
          <label htmlFor="name">Name:</label>
          <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              style={{ marginLeft: '10px', width: '100%' }}
          />
      </div>
      <div style={{ marginBottom: '10px' }}>
          <label htmlFor="email">Email:</label>
          <input
              type="email"
              id="email"
              name="email"
              value={ formData.email }
              onChange={ handleChange }
              style={{ marginLeft: '10px', width: '100%' }}
          />
      </div>
      <div style={{ marginBottom: '10px' }}>
          <label htmlFor="date">Date:</label>
          <input
              type="date"
              id="date"
              name="date"
              value={ formData.date }
              onChange={ handleChange }
              style={{ marginLeft: '10px', width: '100%' }}
          />
      </div>
      <button type="submit" style={{ width: '100%', padding: '8px', background: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' }}>
          Schedule Appointment
      </button>
  </form>
  );
};

export default Appointment;
