import React, { useState, useEffect } from "react";
import "../styles/appointment_styles.css"; // Add your styles here if needed
// import { all } from "../../../backend/Routes/AppointmentRouter";
import { findAllInRenderedTree } from "react-dom/test-utils";

const ModifyAppointment = () => {
  const [formData, setFormData] = useState({
    time: "",
    doctorEmail: "",
    patientEmail: "",
    message: "",
    treatment: "",
    notes: "",
    allergies: "",
    conditions: "",
    medications: "",
    immunizations: "",
    procedures: ""
  });

  // Fetch the appointment details
  const getAppointments = async () => {
    try {
      const response = await fetch("http://localhost:8080/Appointments/getAppointments", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        // Handle non-200 responses
        const errorText = await response.text(); // Get error response as plain text
        console.error("Server error:", errorText);
        alert(`Error: ${response.status} - ${errorText}`);
        return;
      }

      const result = await response.json();

      // Assuming result contains appointment details
      // Update formData state with the fetched data
      setFormData((prevData) => ({
        ...prevData,
        doctorEmail: result.doctorEmail || "",
        time: result.time || "",
        // Add other fields as needed
      }));
    } catch (error) {
      console.error("Error fetching appointment details:", error);
      alert("An error occurred. Please try again.");
    }
  };

  useEffect(() => {
    // Fetch appointment details when the component mounts
    getAppointments();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Retrieve JWT token from storage (e.g., localStorage)
    let token = localStorage.getItem("token"); // Replace with your storage method if different
    if (token && token.startsWith("Bearer ")) {
      token = token.slice(7);
    }

    try {
      const response = await fetch("http://localhost:8080/Appointments/modifyAppointment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: token, // Include JWT token in the Authorization header
        },
        body: JSON.stringify({
          doctorEmail: formData.doctorEmail, // Map `doctor` field to `doctorEmail`
          date: formData.time, // Map `meetingTime` field to `date`
          reason_for_appointment: formData.message, // Map `message` field to `reason_for_appointment`
        }),
      });
  
      if (!response.ok) {
        // Handle non-200 responses
        const errorText = await response.text(); // Get error response as plain text
        console.error("Server error:", errorText);
        alert(`Error: ${response.status} - ${errorText}`);
        return;
      }
  
      const result = await response.json();
      alert(result[0].message); // Show success message
    } catch (error) {
      console.error("Error creating appointment:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
      <form 
    onSubmit={handleSubmit} 
    className="form-card"
    style={{maxHeight: "100vh", maxWidth: "100vh" , overflow: "auto"}} /* Add custom styles here */
  > 
    <div>
      <h2>Change Existing Appointment</h2>
      <text>Feel free to select a time and day to meet your primary care provider!</text>
    </div>

    <div 
      className="form-group"
    >
      <label htmlFor="email">Doctor Email:</label>
      <input
        type="email"
        id="doctorEmail"
        name="doctorEmail"
        value={formData.doctorEmail}
        onChange={handleChange}
        className="form-input" /* Using className for styling the input field */
      />
    </div>
    
    <div 
      className="form-group"
    >
      <label htmlFor="email">Patient Email:</label>
      <input
        type="email"
        id="patientEmail"
        name="patientEmail"
        value={formData.patientEmail}
        onChange={handleChange}
        className="form-input" /* Using className for styling the input field */
      />
    </div>

    <div 
      className="form-group"
    >
      <label htmlFor="time">Date:</label>
      <input
        type="datetime-local"
        id="time"
        name="time"
        min="2024-25-07T00:00"
        max="2030-12-14T00:00"
        value={formData.time}
        onChange={handleChange}
        className="form-input" /* Same styling for this input */
      />
    </div>

    <div 
      className="form-group"
    >
      <label htmlFor="notes">Reason For Appointment:</label>
      <textarea
        id="notes"
        name="notes"
        value={formData.notes}
        onChange={handleChange}
        className="form-input" /* Reusing the form-input class for consistency */
      />
    </div>

    <div className="form-group">
      <label htmlFor="treatment">Treatment:</label>
      <input
        type="text"
        id="treatment"
        name="treatment"
        value={formData.treatment}
        onChange={handleChange}
        className="form-input"
      />
    </div>

    <div className="form-group">
      <label htmlFor="allergies">Notes:</label>
      <input
        type="text"
        id="notes"
        name="notes"
        value={formData.allergies}
        onChange={handleChange}
        className="form-input"
      />
    </div>

    <div className="form-group">
      <label htmlFor="allergies">Allergies:</label>
      <input
        type="text"
        id="allergies"
        name="allergies"
        value={formData.allergies}
        onChange={handleChange}
        className="form-input"
      />
    </div>

    <div className="form-group">
      <label htmlFor="conditions">Conditions:</label>
      <input
        type="text"
        id="conditions"
        name="conditions"
        value={formData.conditions}
        onChange={handleChange}
        className="form-input"
      />
    </div>

    <div className="form-group"> 
      <label htmlFor="medications">Medications:</label>
      <input
        type="text"
        id="medications"
        name="medications"
        value={formData.medications}
        onChange={handleChange}
        className="form-input"
      />
    </div>

    <div className="form-group">
      <label htmlFor="immunizations">Immunizations:</label>
      <input
        type="text"
        id="immunizations"
        name="immunizations"
        value={formData.immunizations}
        onChange={handleChange}
        className="form-input"
      />
    </div>

    <div className="form-group">
      <label htmlFor="procedures">Procedures:</label>
      <input
        type="text"
        id="procedures"
        name="procedures"
        value={formData.procedures}
        onChange={handleChange}
        className="form-input"
      />
    </div>

    <button 
      type="submit"
      className="submit-button" /* Unique class for styling the submit button */
    >
      Modify Appointment
    </button>
  </form>
  );
};

export default ModifyAppointment;
